insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (6, 9785446107728, 'Чистая архитектура. Искусство разработки программного обеспечения', '"Идеальный программист" и "Чистый код" - легендарные бестселлеры Роберта Мартина - рассказывают, как достичь высот профессионализма. "Чистая архитектура" продолжает эту тему, но не предлагает несколько вариантов в стиле "решай сам", а объясняет, что именно следует делать, по какой причине и почему именно такое решение станет принципиально важным для вашего успеха.', '2018-01-01', 1, 100);
insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (5, 9780201616224, 'Программист-прагматик. Путь от подмастерья к мастеру', 'Сегодня очень многие книги по программированию не пытаются донести основы становления не просто "кодером", а программистом более высокого уровня. Они рассказывают про синтаксис или технологии, в то время как с помощью этой книги вы сможете понять, что на самом деле основная сила любой хорошей команды - это наличие способных программистов, которые действительно знают своё дело.', '1999-01-01', 5, 400);
insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (1, 9785496004879, 'Чистый код: создание, анализ и рефакторинг. Библиотека программиста', 'Если ваш код работает, это ещё не значит, что его можно назвать хорошим. Эта книга по программированию расскажет вам не только о том, как писать хороший код, но и как превратить плохой код в хороший. С её помощью вы сможете понять, почему такие мелочи, как правильное название переменных, - вовсе даже не мелочи, а важные пункты на пути к осознанию того, как писать "чистый код".', '2013-05-30', 1, 300);
insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (2, 9785750200641, 'Совершенный код', 'Книга по программированию Стива Макконнелла - это то, что советуют прочитать хоть раз в жизни каждому программисту. Свыше десяти лет первое издание небезосновательно считалось лучшим практическим руководством по кодингу. В ней, опираясь на свой опыт, автор собрал различные методики и принципы программирования. Программист любой области с любым уровнем обязательно найдет для себя что-то, о чём он раньше не знал или не задумывался.', '1993-01-01', 2, 500);
insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (4, 9785845908575, 'Алгоритмы: построение и анализ', 'Эта книга является вводным курсом по современным компьютерным алгоритмам. В ней описано множество действительно работающих алгоритмов на доступном уровне для основной массы интересующихся читателей. Авторы постарались сделать пояснения к каждому, даже довольно сложному, алгоритму максимально доступными для читателя.', '1989-01-01', 4, 200);
insert into lab3.book (id, isbn, title, description, publishing, publisher_id, min_price) values (3, 9780131101630, 'Язык программирования С', 'Книга по программированию от авторов языка C. В своё время она стала своеобразным классическим учебником по языку С и до сих пор является классикой для всех изучающих и/или использующих как С, так и С++.', '1978-01-01', 3, 600);
